package javastandard.variable;

public class VarEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int year = 2021;
		int age = 24;
		System.out.println(year);
		System.out.println(age);
		
		year += 1;
		age += 1;
		
		System.out.println(year);
		System.out.println(age);

	}

}
